mapper.select:
SELECT 
  FROM t_service_provider_settle_date

    id = #{id,jdbcType=NUMERIC}
    AND service_provider_id = #{serviceProviderId,jdbcType=NUMERIC}
    AND settle_date = #{settleDate,jdbcType=TIMESTAMP}

SELECT 
  FROM t_service_provider_settle_date_summary

     id = #{id,jdbcType=NUMERIC}
     AND settle_date = #{settleDate,jdbcType=TIMESTAMP}

SELECT
FROM t_service_provider_settle_date_summary AS tssds
ORDER BY tssds.create_time DESC, tssds.id DESC LIMIT #{startIndex,jdbcType=NUMERIC}, #{pageSize,jdbcType=NUMERIC}
